// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Import the library 'Roles'
import "./Roles.sol";

// Define a contract 'Retailer2Role' to manage this role - add, remove, check
contract Retailer2Role {
    using Roles for Roles.Role;
    // Define 2 events, one for Adding, and other for Removing

    // Define a struct 'Retailer2s' by inheriting from 'Roles' library, struct Role
     Roles.Role private retailer2s;

    // In the constructor make the address that deploys this contract the 1st Retailer2
    constructor() {
        _addRetailer2(msg.sender);
    }

    // Define a modifier that checks to see if msg.sender has the appropriate role
    modifier onlyRetailer2() {
        require(isRetailer2(msg.sender));
        _;
    }

    // Define a function 'isRetailer2' to check this role
    function isRetailer2(address account) public view returns (bool) {
        return retailer2s.has(account);
    }

    // Define a function 'addRetailer2' that adds this role
    function addRetailer2(address account) public onlyRetailer2 {
        _addRetailer2(account);
    }

    // Define a function 'renounceRetailer2' to renounce this role
    function renounceRetailer2() public {
        _removeRetailer2(msg.sender);
    }

    // Define an internal function '_addRetailer2' to add this role, called by 'addRetailer2'
    function _addRetailer2(address account) internal {
        retailer2s.add(account);
    }

    // Define an internal function '_removeRetailer2' to remove this role, called by 'removeRetailer2'
    function _removeRetailer2(address account) internal {
        retailer2s.remove(account); 
    }
}
